int main()
{
	int a[] = { 1,2,4,6,12,13,18,22,28,31 };
	int key = 31;//查找关键字
	int low = 0;
	int high = sizeof(a)/sizeof(a[0]);

	while (low <= high) {
		int mid = (low + high) / 2;
		if (a[mid] == key) {
			cout << "找到" << endl;
			break;
		}
		if (a[mid] > key)high = mid-1;
		else if (a[mid] < key)low = mid+1;
